data.value:
#version 330 core

out vec4 FragColour;

in vec3 fragPos;

uniform vec3 gridCentre;
uniform float max;

void main()
{
    float disx = fragPos.x - gridCentre.x;
    float disz = fragPos.z - gridCentre.z;
    float distance = sqrt(disx*disx + disz*disz);
    FragColour = vec4(0.3f, 0.3f, 0.3f, 1-distance/max);
}

#version 330 core

out vec4 FragColour;

in vec3 fragPos;

uniform vec3 colour;

void main()
{
    FragColour = vec4(colour, 1.0f);
}

#version 330 core

struct Light {
    vec3 pos;
    vec3 colour;
};
uniform Light lights[10];
uniform int numOfLights;

out vec4 FragColour;

in vec3 normal;
in vec3 fragPos;

uniform vec3 objectColour;

void main()
{
    vec3 norm = normalize(normal);
    vec3 result = vec3(0.0f);

    for(int i = 0; i < numOfLights; i++)
    {
        vec3 lightDir = normalize(lights[i].pos - fragPos);
        float diffuseAmount = max(dot(norm,lightDir), 0.0f);
        result += diffuseAmount * lights[i].colour;
    }

    float ambientStrength = 0.0f;
    vec3 ambient = vec3(ambientStrength);
    result = (ambient+result) * objectColour;
    FragColour = vec4(result, 1.0f);
}

#version 330 core

out vec4 FragColour;
uniform vec3 objectColour;

void main()
{
    FragColour = vec4(objectColour, 1.0f);
}

#version 330 core

out vec4 FragColour;

in vec4 colour;
in vec2 texCoord;

uniform sampler2D ourTexture;

void main()
{
  FragColour = texture(ourTexture, texCoord) * colour;
}

#version 330 core

layout (location = 0) in vec3 aPos;

out vec3 fragPos;

uniform mat4 model;
uniform mat4 view;
uniform mat4 projection;

void main()
{
    gl_Position = projection * view * model * vec4(aPos.x, aPos.y, aPos.z, 1.0f);
    fragPos = vec3(model * vec4(aPos, 1.0));
}

#version 330 core

layout (location = 0) in vec3 aPos;
layout (location = 1) in vec3 aNormal;

out vec3 normal;
out vec3 fragPos;

uniform mat4 model;
uniform mat4 view;
uniform mat4 projection;

void main()
{
    gl_Position = projection * view * model * vec4(aPos.x, aPos.y, aPos.z, 1.0f);
    normal = aNormal;
    fragPos = vec3(model * vec4(aPos, 1.0));
}

#version 330 core

layout (location = 0) in vec3 aPos;

uniform mat4 model;
uniform mat4 view;
uniform mat4 projection;

void main()
{
    gl_Position = projection * view * model * vec4(aPos.x, aPos.y, aPos.z, 1.0f);
}

#version 330 core

layout (location = 0) in vec2 aPos;
layout (location = 1) in vec2 aTexCoord;
layout (location = 2) in vec4 colour;

out vec4 ColourOut;
out vec2 TexCoordOut;

void main()
{
  gl_Position = vec4(aPos, 0.0f, 1.0f);
  ColourOut = colour;
  TexCoordOut = aTexCoord;
}
